sysauto_script.script:
var recordsToMake = 800;
var i;
var rt = ["hr", "facilities","legal"]; //request type
var whatneed = [["Human Resources 1","Human Resources 1"],["Facilities 1","Facilities 2"],["Legal 1", "Legal 2"]];
var indexA = 1, indexB = 90; //days for generate

for(i = 0; i < recordsToMake; i++){
	var gr = new GlideRecord("x_58872_needit_needit");
	var gdt = new GlideDateTime("2020-05-01 00:00:00");
	var dayAddRandom = Math.floor((Math.random() * indexB) + indexA);
	var indexRt = Math.floor((Math.random() * 3));
	var rtRandom = rt[indexRt];
	var wnRandom = whatneed[indexRt][Math.floor((Math.random() * 2))];
	gdt.addDaysLocalTime( dayAddRandom );

	//gs.info(gdt.getLocalDate());
	gr.initialize();
	gr.u_request_type = rtRandom;
	gr.u_what_needed = wnRandom;
	gr.u_when_needed = gdt.getLocalDate();
	gr.short_description = "example 2";
	gr.state = "13";
	gr.priority = "4";
	gr.u_requested_for = "System Administrator";
	gr.insert();
}
